import { Html5QrcodeScanner } from "html5-qrcode";
import { useEffect, useState, useRef } from "react";
import './App.css';
function App() {

  const [scanResult, setScanResult] = useState(null);
  const [height, setHeight] = useState(0)
  const ref = useRef(170)

  useEffect(() => {
    setHeight(ref.current.clientHeight * 0.3)
  })



  useEffect(() => { 
    console.log(height)
    const scanner  = new Html5QrcodeScanner('reader', {
      qrbox: { width: height, height: height },
      fps: 10,  
  });

  scanner.render(onScanSuccess, onScanError);

  function onScanSuccess(qrCodeMessage) {
      // handle on success condition with the decoded message
      scanner.clear(); // stop scanning
      setScanResult(qrCodeMessage);
      console.log(qrCodeMessage);
  }

  function onScanError(errorMessage) {  
      // handle on error condition with the error message
      console.warn(errorMessage); 
  }
}, []);




  return (
    <div className="App">
      <h1>QR Code Scanning in React</h1>
      <div className="container">
        {
          scanResult 
          ? <div id="reader">Success: {scanResult}</div>
          : <div id="reader"></div>

        }
      </div>

    </div>
  );
}

export default App;
